import React from 'react';
import { Tab } from 'semantic-ui-react';
import getData from '../util/getData.js';
import AllDegrees from './AllDegrees.js';


export default class DegreeTabs extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            degrees: {},
            loaded: false
        }
    }

    render(){
        const {degrees, loaded} = this.state;


        const paneData = [
            {
              title: 'Graduate',
              group: degrees.graduate
            },
            {
              title: 'Undergraduate',
              group: degrees.undergaduate
            }
          ];

        const panes = [
            { menuItem: 'Undergraduate', render: () => 
                <div className='tab'> 
                    <Tab.Pane>
                        <AllDegrees activeClass="activeTab" key="1" title="Undergraduate" degGroup={degrees.undergraduate}/>
                    </Tab.Pane> 
                </div>
            },
            { menuItem: 'Graduate', render: () => 
                <div className='tab'>
                    <Tab.Pane>
                        <AllDegrees activeClass="activeTab" key="2" title="Graduate" degGroup={degrees.graduate}/>
                    </Tab.Pane> 
                </div>
            }
        ]

        if(!loaded)return(<div><h1>Our Degrees are loading...</h1></div>);

        return(
            <div className='optionTabs'>
                <div className="subTitle"><h2>Degrees</h2></div>
                <Tab panes={panes} />
            </div>

        );
    }
