        //             //   key={key}
        //               title={title}
        //               degGroup={degGroup}
        //             />
        //           </Tab.Pane> 
        //         </div>
        //       );
        //     };

        //   return {
        //     menuItem: title,
        //     render: renderPane(title, index, group)
        //   };
    // });

    componentDidMount(){
        getData('degrees/')
            .then((json) => {
                this.setState({
                    degrees:json,
                    loaded: true
                })
            })
    }
}
