import { DataGrid } from '@mui/x-data-grid';
import getData from '../util/getData';


export default class Courses extends React.Component{

  constructor(props){
    super(props);
    this.state = {
        courses:{},
        loaded:false
    }
  } 

render() {
  const columns = [
    { field: 'id', headerName: 'ID', width: 100 },
    {
      field: 'employer',
      headerName: 'Employer',
      width: 150,
      editable: true,
    },
    {
      field: 'degree',
