      width: 150,
      editable: true,
    },
    {
      field: 'city',
      headerName: 'City',
      width: 150,
      editable: true,
    },
    {
      field: 'term',
      headerName: 'Term',
      width: 150,
      editable: true,
    },
  ];

  courses.coopTable.map(({ title, group }) => {

    return (
      <div className='tab'>
         <Box className='gridBox'>
            <DataGrid
              columns={columns}
              pageSize={5}
              rowsPerPageOptions={[5]}
              checkboxSelection
              disableSelectionOnClick
              experimentalFeatures={{ newEditingApi: true }}
            />
    </Box>
      </div>
    );

